@import "font.css";

@tailwind base;
@tailwind components;
@tailwind utilities;

@layer base {
  html {
    @apply overflow-x-hidden;
  }
  b {
    @apply font-bold;
  }
  eb {
    @apply font-extrabold;
  }
  section {
    @apply break-keep m-auto;
  }
  article {
    @apply break-keep m-auto;
  }
  ::selection {
    @apply bg-primary-500 text-white;
  }
  ::-moz-selection {
    @apply bg-primary-500 text-white;
  }
  img {
    -webkit-user-drag: none;
  }
  h1,
  h2,
  h3,
  h4,
  h5,
  h6,
  p,
  span {
    @apply rotate-[0.03deg];
  }
}

@layer components {
  /* 사용자 정의 */
  .rowCenter {
    @apply flex flex-row items-center justify-center;
  }
  .colCenter {
    @apply flex flex-col items-center justify-center;
  }
  .sectionWrapper {
    @apply colCenter break-keep container mx-auto;
  }
  /* -------- */

  /* 헤더 정의 */
  .navText {
    @apply after:hover:border-b-[.1563rem] hover:after:animate-[navAnimate] hover:after:animate-duration-200 hover:after:animate-ease-in-out after:hover:border-white after:hover:absolute after:hover:w-full after:hover:-bottom-[.1875rem] after:hover:left-0 relative transition-all ease-in-out;
  }
  @keyframes navAnimate {
    0% {
      width: 0;
    }
    10% {
      width: 10%;
    }
    20% {
      width: 20%;
    }
    30% {
      width: 30%;
    }
    40% {
      width: 40%;
    }
    50% {
      width: 50%;
    }
    60% {
      width: 60%;
    }
    70% {
      width: 70%;
    }
    80% {
      width: 80%;
    }
    90% {
      width: 90%;
    }
    100% {
      width: 100%;
    }
  }
  /* -------- */

  /* 섹션1 정의 */
  .mainTextAfter {
    @apply after:absolute after:lg:top-[13%] after:left-0 after:lg:left-[72%] after:z-30 after:content-[url('/img/main_visual_text_spin.webp')] after:animate-spin after:animate-ease-linear after:animate-duration-[7000ms];
  }
  .mainScrollMouse {
    @apply w-6 h-10 border-2 border-white rounded-full relative before:absolute before:top-2 before:left-[calc(50%-2px)] before:size-[5px] before:bg-white before:rounded-full before:animate-ease-in-out before:animate-[mouseScroll] before:animate-duration-[2000ms] before:animate-infinite;
  }

  @keyframes mouseScroll {
    0% {
      transform: translate(0, 0);
      opacity: 0;
    }
    40% {
      opacity: 1;
    }
    80% {
      transform: translate(0, 20px);
      opacity: 0;
    }
    100% {
      opacity: 0;
    }
  }
  /* -------- */

  /* 섹션2 정의 */
  .cls-1 {
    @apply animate-pulse animate-ease-in-out animate-infinite animate-duration-[1000ms];
  }
  .creative-cls-1 {
    @apply cls-1;
  }
}

@layer utilities {
  /* 우선 적용 */
  .sr-only {
    clip: rect(0, 0, 0, 0) !important;
    border-width: 0 !important;
    @apply absolute w-[1px] h-[1px] p-0 -m-[1px] overflow-hidden whitespace-nowrap z-[-999999] opacity-0 !important;
  }
  @media screen(lg) {
    .mo-only {
      @apply hidden !important;
    }
  }
  @media screen and (max-width: 1024px) {
    .pc-only {
      @apply hidden !important;
    }
  }
}

